import React, { useState, useEffect } from "react";
import ResultsTable from "./ResultsTable";
import ExportButton from "./ExportButton";
import db from "../firebase";
import { collection, getDocs } from "firebase/firestore";

const dropdownStyle = {
  width: "100%",
  padding: "80px",
  marginBottom: "20px",
  borderRadius: "5px",
  border: "1px solid #ccc",
  fontSize: "15px",
  lineHeight: "1.5",
};

const CourseFilter = () => {
  const [countries, setCountries] = useState([]);
  const [institutions, setInstitutions] = useState([]);
  const [subjectAreas, setSubjectAreas] = useState([]);
  const [pacificCourseCode, setPacificCourseCode] = useState("");
const [pacificSubjectAreas, setPacificSubjectAreas] = useState([]);
  const [results, setResults] = useState([]);
  const [showTooltip, setShowTooltip] = useState(false);
  const [selectedCountry, setSelectedCountry] = useState("");
const [selectedInstitution, setSelectedInstitution] = useState("");
const [selectedSubjectArea, setSelectedSubjectArea] = useState("");
const [selectedSchool, setSelectedSchool] = useState("");
const [selectedPacificSubjectArea, setSelectedPacificSubjectArea] = useState("");

  const handleFilter = () => {
  console.log("Search clicked");
};

  useEffect(() => {
    const fetchData = async () => {
      const snapshot = await getDocs(collection(db, "Courses"));
      const docs = snapshot.docs.map((doc) => doc.data());

      const uniqueCountries = [...new Set(docs.map((doc) => doc.country))];

   const uniqueInstitutions = [
  ...new Set(
    docs
      .map((doc) => doc.partner_university?.trim().toLowerCase()) // normalize case
      .filter(Boolean)
  ),
].map((name) => name.charAt(0).toUpperCase() + name.slice(1)); // capitalize first letter
      const uniqueSubjects = [...new Set(docs.map((doc) => doc.pacific_course_name))];

      setCountries(uniqueCountries.sort());
      setInstitutions(uniqueInstitutions.sort());
      setSubjectAreas(uniqueSubjects.sort());
    };

    fetchData();
  }, []);

 const handleClear = () => {
  setSelectedCountry("");
  setSelectedInstitution("");
  setSelectedSubjectArea("");
  setSelectedSchool("");
  setSelectedPacificSubjectArea("");
  setPacificCourseCode("");
  setResults([]);
  setShowTooltip(false);
};

  return (
    <div
      style={{
        fontFamily: "Neuzeit Grotesk, sans-serif",
        maxWidth: "1100px",
        margin: "40px auto",
        padding: "20px",
      }}
    >
      <div
        style={{
          display: "flex",
          justifyContent: "space-between",
          gap: "40px",
          alignItems: "flex-start",
        }}
      >
        {/* International Courses Section */}
        <div style={{ flex: 1 }}>
  <h3 style={{ color: "#0a0b0bff", fontSize: "30px", marginBottom: "20px" }}>
    International Courses
  </h3>

  <label>Country:</label>
  <select
    value={selectedCountry}
    onChange={(e) => setSelectedCountry(e.target.value)}
    style={dropdownStyle}
  >
    <option value="">Select Country</option>
    {countries.map((c) => (
      <option key={c} value={c}>{c}</option>
    ))}
  </select>

  <label>University / Institution:</label>
  <select
    value={selectedInstitution}
    onChange={(e) => setSelectedInstitution(e.target.value)}
    style={dropdownStyle}
  >
    <option value="">Select Institution</option>
    {institutions.map((u) => (
      <option key={u} value={u}>{u}</option>
    ))}
  </select>

      <label>Subject Area:</label>
      <select
        value={selectedSubjectArea}
        onChange={(e) => setSelectedSubjectArea(e.target.value)}
        style={dropdownStyle}
      >
      <option value="">Select Subject Area</option>
      {subjectAreas.map((s) => (
        <option key={s} value={s}>{s}</option>
      ))}
  </select>
</div>

        {/* Pacific Equivalent Courses Section */}
        <div style={{ flex: 1 }}>
          <h3 style={{ color: "#0a0b0bff", fontSize: "30px", marginBottom: "20px" }}>
            Pacific Equivalent Courses
          </h3>

          <label>School:</label>
          <select
            value={selectedSchool}
            onChange={(e) => setSelectedSchool(e.target.value)}
            style={dropdownStyle}
          >
            <option value="">Select UOP School</option>
            <option>Arthur A. Dugoni School of Dentistry</option>
            <option>Benerd College</option>
            <option>College of the Pacific</option>
            <option>Conservatory of Music</option>
            <option>Eberhardt School of Business</option>
            <option>McGeorge School of Law</option>
            <option>School of Engineering and Computer Science</option>
            <option>School of Health Sciences</option>
            <option>Thomas J. Long School of Pharmacy</option>
          </select>

          <label>Subject Area:</label>
          <select style={dropdownStyle}>
            <option value="">Select Subject Area</option>
          </select>

          <label>Course Code:</label>

           <input
          type="text"
          value={pacificCourseCode}
          onChange={(e) => setPacificCourseCode(e.target.value)}
          placeholder="e.g., HIST 120"
          style={{
            ...dropdownStyle,
            padding: "10px",
            fontSize: "15px",
          }}
        />

        </div>


      </div>


      {/* Buttons */}
      <div style={{ display: "flex", justifyContent: "center", marginTop: "30px", gap: "20px" }}>
        {/* Search Button with Tooltip */}
        <div style={{ position: "relative", display: "inline-block" }}>
          {showTooltip && (
            <div
              style={{
                position: "absolute",
                top: "-45px",
                left: "50%",
                transform: "translateX(-50%)",
                backgroundColor: "black",
                color: "white",
                padding: "8px 12px",
                borderRadius: "6px",
                fontSize: "14px",
                whiteSpace: "nowrap",
                zIndex: 1000,
              }}
            >
              Please be more specific in your search
            </div>
          )}
          <button
            onClick={handleFilter}
            onMouseEnter={() => setShowTooltip(true)}
            onMouseLeave={() => setShowTooltip(false)}
            style={{
              backgroundColor: "#FF671D",
              color: "#fff",
              border: "none",
              padding: "10px 28px",
              fontWeight: "bold",
              fontSize: "16px",
              borderRadius: "6px",
              width: "100px",
              cursor: "pointer",
            }}
          >
            Search
          </button>
        </div>

        {/* Clear Button */}
        <button
          onClick={handleClear}
          style={{
            backgroundColor: "#A29889",
            color: "#fff",
            border: "none",
            padding: "10px 28px",
            fontWeight: "bold",
            fontSize: "16px",
            borderRadius: "6px",
            width: "100px",
            cursor: "pointer",
          }}
        >
          Clear
        </button>
      </div>

      <ResultsTable results={results} />
      <ExportButton results={results} />
    </div>
  );
};

export default CourseFilter;
