import React, { useState, useEffect } from "react";
import ResultsTable from "./ResultsTable";
import ExportButton from "./ExportButton";
import db from "../firebase";
import { collection, getDocs } from "firebase/firestore";

const dropdownStyle = {
  width: "100%",
  maxWidth: "300px",
  padding: "10px",
  marginBottom: "20px",
  borderRadius: "5px",
  border: "1px solid #ccc",
  fontSize: "15px",
  lineHeight: "1.5",
};

const CourseFilter = () => {
  const [countries, setCountries] = useState([]);
  const [institutions, setInstitutions] = useState([]);
  const [subjectAreas, setSubjectAreas] = useState([]);
  const [pacificCourseCode, setPacificCourseCode] = useState("");
  const [pacificSubjectAreas, setPacificSubjectAreas] = useState([]);
  const [results, setResults] = useState([]);
  const [showTooltip, setShowTooltip] = useState(false);
  const [selectedCountry, setSelectedCountry] = useState("");
  const [selectedInstitution, setSelectedInstitution] = useState("");
  const [selectedSubjectArea, setSelectedSubjectArea] = useState("");
  const [selectedSchool, setSelectedSchool] = useState("");
  const [selectedPacificSubjectArea, setSelectedPacificSubjectArea] = useState("");

  const handleFilter = () => {
    console.log("Search clicked");
  };

  useEffect(() => {
  const fetchCountries = async () => {
    try {
      const response = await fetch("https://restcountries.com/v3.1/all");
      const data = await response.json();
      const countryNames = data.map((country) => country.name.common);
      setCountries(countryNames.sort());
    } catch (error) {
      console.error("Error fetching countries:", error);
    }
  };

  const fetchFirebaseData = async () => {
    const snapshot = await getDocs(collection(db, "Courses"));
    const docs = snapshot.docs.map((doc) => doc.data());

    const uniqueInstitutions = [
      ...new Set(
        docs.map((doc) => doc.partner_university?.trim().toLowerCase()).filter(Boolean)
      ),
    ].map((name) => name.charAt(0).toUpperCase() + name.slice(1));

    const uniqueSubjects = [...new Set(docs.map((doc) => doc.pacific_course_name))];

    setInstitutions(uniqueInstitutions.sort());
    setSubjectAreas(uniqueSubjects.sort());
  };

  fetchCountries();         // Fetch country list from API
  fetchFirebaseData();      // Fetch institution and subject area from Firebase
}, []);
  const handleClear = () => {
    setSelectedCountry("");
    setSelectedInstitution("");
    setSelectedSubjectArea("");
    setSelectedSchool("");
    setSelectedPacificSubjectArea("");
    setPacificCourseCode("");
    setResults([]);
    setShowTooltip(false);
  };

  return (
    <div
  style={{
    display: "grid",
    gridTemplateColumns: "1fr 1fr",
    gap: "40px",
    alignItems: "start",
    marginBottom: "30px",
  }}
>

   <div
  style={{
    display: "grid",
    gridTemplateColumns: "1fr 1fr",
    gap: "80px",
    justifyContent: "center",
    alignItems: "start",
    marginBottom: "40px",
  }}
>  

       {/* International Courses Section */}
  <div>
    <h3 style={{ color: "#0a0b0bff", fontSize: "24px", marginBottom: "15px" }}>
      International Courses
    </h3>

    <label>Country:</label>
    <select value={selectedCountry} onChange={(e) => setSelectedCountry(e.target.value)} style={dropdownStyle}>
      <option value="">Select Country</option>
      {countries.map((c) => <option key={c} value={c}>{c}</option>)}
    </select>

    <label>University / Institution:</label>
    <select value={selectedInstitution} onChange={(e) => setSelectedInstitution(e.target.value)} style={dropdownStyle}>
      <option value="">Select Institution</option>
      {institutions.map((u) => <option key={u} value={u}>{u}</option>)}
    </select>

    <label>Subject Area:</label>
    <select value={selectedSubjectArea} onChange={(e) => setSelectedSubjectArea(e.target.value)} style={dropdownStyle}>
      <option value="">Select Subject Area</option>
      {subjectAreas.map((s) => <option key={s} value={s}>{s}</option>)}
    </select>
  </div>

  {/* Pacific Equivalent Courses Section */}
  <div>
    <h3 style={{ color: "#0a0b0bff", fontSize: "24px", marginBottom: "15px" }}>
      Pacific Equivalent Courses
    </h3>

    <label>School:</label>
<select
  value={selectedSchool}
  onChange={(e) => setSelectedSchool(e.target.value)}
  style={dropdownStyle}
>
  <option value="">Select UOP School</option>
  <option value="College of the Pacific">College of the Pacific</option>
  <option value="Eberhardt School of Business">Eberhardt School of Business</option>
  <option value="School of Engineering and Computer Science">School of Engineering and Computer Science</option>
  <option value="Thomas J. Long School of Pharmacy">Thomas J. Long School of Pharmacy</option>
  <option value="School of International Studies">School of International Studies</option>
  <option value="Benerd College">Benerd College</option>
  <option value="Conservatory of Music">Conservatory of Music</option>
  <option value="School of Health Sciences">School of Health Sciences</option>
</select>

    <label>Pacific Subject Area:</label>
    <select value={selectedPacificSubjectArea} onChange={(e) => setSelectedPacificSubjectArea(e.target.value)} style={dropdownStyle}>
      <option value="">Select Subject Area</option>
      {/* Add options */}
    </select>

    <label>Course Code:</label>
    <input
      type="text"
      value={pacificCourseCode}
      onChange={(e) => setPacificCourseCode(e.target.value)}
      placeholder="e.g., HIST 120"
      style={dropdownStyle}
    />
  </div>
</div>


      {/* Buttons */}

     <div
  style={{
    display: "flex",
    justifyContent: "center",
    gap: "20px",
    marginTop: "20px",
  }}
>
  {/* Search Button */}
  <div style={{ position: "relative" }}>
    {showTooltip && (
      <div
        style={{
          position: "absolute",
          top: "-45px",
          left: "50%",
          transform: "translateX(-50%)",
          backgroundColor: "black",
          color: "white",
          padding: "8px 12px",
          borderRadius: "6px",
          fontSize: "14px",
          whiteSpace: "nowrap",
          zIndex: 1000,
        }}
      >
        Please be more specific in your search
      </div>
    )}
    <button
      onClick={handleFilter}
      onMouseEnter={() => setShowTooltip(true)}
      onMouseLeave={() => setShowTooltip(false)}
      style={{
        backgroundColor: "#FF671D",
      color: "#fff",
      border: "none",
      padding: "10px 28px",
      fontWeight: "bold",
      fontSize: "16px",
      borderRadius: "6px",
      width: "100px",
      cursor: "pointer",
      }}
    >
      Search
    </button>
  </div>

  {/* Clear Button */}
  <button
    onClick={handleClear}
    style={{
      backgroundColor: "#A29889",
      color: "#fff",
      border: "none",
      padding: "10px 28px",
      fontWeight: "bold",
      fontSize: "16px",
      borderRadius: "6px",
      width: "100px",
      cursor: "pointer",
    }}
  >
    Clear
  </button>
</div>

      <ResultsTable results={results} />
      <ExportButton results={results} />
    </div>
  );
};

export default CourseFilter;
