import React, { useState } from "react";
const UniversityAccordion = ({ universities, selectedCountry }) => {
  return (
    <div style={{ maxWidth: "800px", margin: "40px auto" }}>

      {universities.map((uni, index) => {
        const [open, setOpen] = useState(false); // Local to each accordion
        return (
  <div
    style={{
      maxWidth: "1100px", // Match dropdown width
      margin: "30px auto", // Center it
      backgroundColor: "#fff",
      borderRadius: "8px",
      padding: "20px",
      boxShadow: "0 2px 10px rgba(0, 0, 0, 0.05)",
    }}
  >
    <h2
      style={{
        fontSize: "24px",
        color: "#004b87",
        fontWeight: "bold",
        marginBottom: "20px",
        fontFamily: "Ramaraja, serif",
        textAlign: "center",
      }}
    >
      Universities in {selectedCountry}:
    </h2>

    {universities.map((uni, index) => (
      <details
        key={index}
        style={{
          marginBottom: "10px",
          backgroundColor: "#f9f9f9",
          border: "1px solid #ddd",
          borderRadius: "4px",
          padding: "12px",
        }}
      >
        <summary
          style={{
            fontWeight: "bold",
            color: "#ff6f00ff",
            fontSize: "16px",
            cursor: "pointer",
          }}
        >
          {uni.partner_university} ({uni.country})
        </summary>
        {/* You can add more info here inside details */}
      </details>
    ))}
  </div>
);

      })}
    </div>
  );
};

export default UniversityAccordion;
