import React from "react";

const InfoSection = () => {
  return (
    <div style={{ width: "100%" }}>

      <div style={{ backgroundColor: "#231f20", padding: "2px 0" , height: "90px"}}>
        <img
          src="/assets/logo.png"
          alt="University of the Pacific Logo"
          style={{ width: "2in", marginTop: "5px", marginLeft: "10px" }}
        />
      </div>


      <img
        src="/assets/Uop Banner.jpg"
        alt="Pacific Campus Banner"
  style={{
    width: "100%",
    height: "250px",         // ðŸ‘ˆ Set a fixed height
    objectFit: "cover",      // ðŸ‘ˆ Crop image nicely without distortion
    display: "block",
  }}

      />

      <h1
    style={{
      position: "absolute",
      top: "50%",
      left: "50%",
      transform: "translate(-50%, -50%)",
      color: "white",
      fontSize: "36px",
      fontWeight: "bold",
      textShadow: "2px 2px 5px rgba(0,0,0,0.6)",
      fontFamily: "Georgia, serif",
      backgroundColor: "rgba(0, 0, 0, 0.3)",
      padding: "10px 20px",
      borderRadius: "10px",
    }}
  >
    Study Abroad Course Search
  </h1>

      <div
        style={{
          fontFamily: "Neuzeit Grotesk, sans-serif",
          fontSize: "13pt",
          lineHeight: "17pt",
          color: "#231f20",
          maxWidth: "900px",
          margin: "40px auto",
        }}
      >



      </div>
    </div>
  );
};

export default InfoSection;
