import React from "react";

const UniversityAccordion = ({ universities }) => {
  if (!universities || universities.length === 0) return null;

  // Extract the country name from the first entry
  const country = universities[0].country;

  return (
    <div
      style={{
        maxWidth: "31000px",
        width: "58%",
        margin: "40px auto",
        fontFamily: "Neuzeit Grotesk Regular, Ramaraj Regular",
        padding: "20px",
        backgroundColor: "#fff",
        borderRadius: "10px",
        boxShadow: "0 4px 12px rgba(0, 0, 0, 0.06)",

      }}
    >


      {universities.map((uni, idx) => (
       <details
      key={idx}
      style={{
        marginBottom: "10px", // increased spacing
        backgroundColor: "#ffffff",
        border: "1px solid #ddd",
        borderRadius: "6px",
        padding: "0", // remove padding from <details>, move it to <summary>
        overflow: "hidden",
      }}
    >
    <summary
      style={{
        fontWeight: "bold",
        color: "#212322",
        fontSize: "17px",
        cursor: "pointer",
        padding: "20px 24px", // more padding
        minHeight: "50px", // increased height
        display: "flex",
        alignItems: "center", // vertically center content
        backgroundColor: "#f9f9f9", // optional: make it pop
      }}
    >
    {uni.partner_university} ({uni.country})
  </summary>


          <div style={{ marginTop: "10px", paddingLeft: "10px" }}>
            <p><strong>Partner Course Code:</strong> {uni.partner_course_code}</p>
            <p><strong>Partner Course Name:</strong> {uni.partner_course_name}</p>
            <p><strong>Pacific Course Code:</strong> {uni.pacific_course_code}</p>
            <p><strong>Pacific Course Name:</strong> {uni.pacific_course_name}</p>
          </div>
        </details>
      ))}
    </div>
  );
};

export default UniversityAccordion;
