import React, { useState, useEffect } from "react";
import ResultsTable from "./ResultsTable";
import ExportButton from "./ExportButton";
import UniversityAccordion from "./UniversityAccordion";

const CourseFilter = () => {
  const [countries, setCountries] = useState([]);
  const [institutions, setInstitutions] = useState([]);
  const [subjectAreas, setSubjectAreas] = useState([]);
  const [pacificCourseCode, setPacificCourseCode] = useState("");
  const [pacificSubjectAreas, setPacificSubjectAreas] = useState([]);
  const [uopSchools, setUopSchools] = useState([]);
  const [results, setResults] = useState([]);
  const [selectedCountry, setSelectedCountry] = useState("");
  const [selectedInstitution, setSelectedInstitution] = useState("");
  const [selectedSubjectArea, setSelectedSubjectArea] = useState("");
  const [selectedSchool, setSelectedSchool] = useState("");
  const [selectedPacificSubjectArea, setSelectedPacificSubjectArea] = useState("");
  const [allCourses, setAllCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filteredUniversities, setFilteredUniversities] = useState([]);
  const [searchClicked, setSearchClicked] = useState(false);

  const dropdownStyle = {
    width: "100%",
    maxWidth: "500px",
    padding: "14px",
    borderRadius: "6px",
    border: "1px solid #ccc",
    fontSize: "17px",
    marginBottom: "20px",
    backgroundColor: "#fff",
    appearance: "none",
    WebkitAppearance: "none",
  };

  const handleClear = () => {
    setSelectedCountry("");
    setSelectedInstitution("");
    setSelectedSubjectArea("");
    setSelectedSchool("");
    setSelectedPacificSubjectArea("");
    setPacificCourseCode("");
    setResults([]);
    setSearchClicked(false);
  };

  useEffect(() => {
    const loadAll = async () => {
      try {
        const schoolsRes = await fetch("https://gist.githubusercontent.com/farheen-shaikh530/babe5bf8a55e7648e6c5176ce3de2860/raw/");
        const pacificSubjectsRes = await fetch("https://gist.githubusercontent.com/farheen-shaikh530/22fbd4686a5cd14cc92781038b0f8cfa/raw/");
        const equivalentsRes = await fetch("https://gist.githubusercontent.com/farheen-shaikh530/714ed972644392cff226383f5de66b66/raw/f52d7566afaf4105a2eb725bf2b1feb69c34fc57/international_universities.json");

        const schoolsData = await schoolsRes.json();
        const pacificSubjectsData = await pacificSubjectsRes.json();
        const courseEquivalentsData = await equivalentsRes.json();

        setUopSchools(schoolsData.schools);
        setPacificSubjectAreas(pacificSubjectsData.subjects);
        setAllCourses(courseEquivalentsData.universities);

        const uniqueCountries = [...new Set(courseEquivalentsData.universities.map(c => c.country))];
        const uniqueInstitutions = [...new Set(courseEquivalentsData.universities.map(c => c.partner_university))];

        setCountries(uniqueCountries.sort());
        setInstitutions(uniqueInstitutions.sort());
      } catch (error) {
        console.error("Error loading course data:", error);
      } finally {
        setLoading(false);
      }
    };
    loadAll();
  }, []);

  useEffect(() => {
    if (selectedCountry && allCourses.length > 0) {
      const matchedUnis = allCourses
        .filter(course => course.country === selectedCountry)
        .map(course => ({ partner_university: course.partner_university, country: course.country }));

      const uniqueUnis = Array.from(new Set(matchedUnis.map(u => JSON.stringify(u)))).map(u => JSON.parse(u));
      setFilteredUniversities(uniqueUnis);
    } else {
      setFilteredUniversities([]);
    }
  }, [selectedCountry, allCourses]);

  const handleFilter = () => {
    const filtered = allCourses.filter(course => {
      return (
        (!selectedCountry || course.country === selectedCountry) &&
        (!selectedInstitution || course.partner_university === selectedInstitution) &&
        (!selectedSubjectArea || course.partner_course_subject === selectedSubjectArea) &&
        (!selectedSchool || course.pacific_school === selectedSchool) &&
        (!selectedPacificSubjectArea || course.pacific_subject_area === selectedPacificSubjectArea) &&
        (!pacificCourseCode || course.pacific_course_code.toLowerCase().includes(pacificCourseCode.toLowerCase()))
      );
    });
    setSearchClicked(true);
    setResults(filtered);
  };

  return (
    <div style={{ fontFamily: "Neuzeit Grotesk Regular, Ramaraj Regular", maxWidth: "1200px", margin: "0 auto", padding: "40px 20px" }}>
      <div style={{ display: "flex", justifyContent: "center", gap: "80px", marginBottom: "40px" }}>
        <div style={{ flex: 1 }}>
          <h3>International Courses</h3>
          <label>Country:</label>
          <select value={selectedCountry} onChange={(e) => setSelectedCountry(e.target.value)} style={dropdownStyle}>
            <option value=""> </option>
            {countries.map((c, i) => <option key={i} value={c}>{c}</option>)}
          </select>

          <label>University / Institution:</label>
          <select value={selectedInstitution} onChange={(e) => setSelectedInstitution(e.target.value)} style={dropdownStyle}>
            <option value=""> </option>
            {institutions.map((i, index) => <option key={index} value={i}>{i}</option>)}
          </select>

          <label>Subject Area:</label>
          <select value={selectedSubjectArea} onChange={(e) => setSelectedSubjectArea(e.target.value)} style={dropdownStyle}>
            <option value=""> </option>
            {subjectAreas.map((s, i) => <option key={i} value={s}>{s}</option>)}
          </select>
        </div>

        <div style={{ flex: 1 }}>
          <h3>UOP Equivalent Courses</h3>
          <label>School:</label>
          <select value={selectedSchool} onChange={(e) => setSelectedSchool(e.target.value)} style={dropdownStyle}>
            <option value=""> </option>
            {uopSchools.map((s, i) => <option key={i} value={s}>{s}</option>)}
          </select>

          <label>Pacific Subject Area:</label>
          <select value={selectedPacificSubjectArea} onChange={(e) => setSelectedPacificSubjectArea(e.target.value)} style={dropdownStyle}>
            <option value=""> </option>
            {pacificSubjectAreas.map((s, i) => <option key={i} value={s}>{s}</option>)}
          </select>

          <label>Course Code:</label>
          <input
            type="text"
            value={pacificCourseCode}
            onChange={(e) => setPacificCourseCode(e.target.value)}
            placeholder="e.g., HIST 120"
            style={{ padding: "10px", fontSize: "16px", borderRadius: "6px", border: "1px solid #ccc", width: "100%" }}
          />
        </div>
      </div>

      <div style={{ display: "flex", justifyContent: "center", gap: "20px" }}>
        <button
          onClick={handleFilter}
          disabled={!selectedCountry && !selectedInstitution && !selectedSubjectArea}
          style={{ backgroundColor: "#E65300", color: "#fff", padding: "10px 28px", fontWeight: "bold", borderRadius: "6px", cursor: "pointer" }}
        >
          Search
        </button>
        <button
          onClick={handleClear}
          style={{ backgroundColor: "#A29889", color: "#fff", padding: "10px 28px", fontWeight: "bold", borderRadius: "6px", cursor: "pointer" }}
        >
          Clear
        </button>
      </div>

      {searchClicked && filteredUniversities.length > 0 && allCourses.length > 0 && (
        <UniversityAccordion universities={filteredUniversities} allCourses={allCourses} />
      )}

      <ExportButton results={results} />
    </div>
  );
};

export default CourseFilter;
